INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('28','E-Mail','en','This page enables the Administrators to send messages to users, email addresses and security roles.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('90','Info System','en','The module show the number of active connections to the server, the server up time, the number of ASP.NET restart, the number of memory cashe used and the amount of free memory left.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('72','Host settings','en','Management of Host setting.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('14','Paramètres du site','fr','Les paramètres du site représentent les options locales pour votre portail. Le module vous permet d''adapter votre portail pour répondre à vos exigences.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('64','Gérer les modules','fr','Vous pouvez modifier les noms ainsi que les définitions des modules.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('15','Gestion usagers','fr','Le module sert à gérer les utilisateurs enregistrés. Il vous permet d''ajouter, modifier et supprimer des comptes utilisateurs ainsi que de contrôler leurs profils de sécurité.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('19','Vendors','en','This page manages Vendor accounts within this web site.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('27','Statistiques','fr','Le module sert à voir les statistiques d''utilisations de votre portail.  Plus particulièrement, qui utilise le site, quand et comment.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('76','Gallery','en','The Gallery module manages multiple media formats.  Media can be sorted in folders and can be displayed in different views including a slideshow.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('21','Gestion fichiers','fr','Le module sert à gérer les fichiers stockés sur le portail web. Le module vous permet de télécharger, supprimer et de synchroniser la liste des fichiers avec la basse de données SQL. Il fournit également des informations sur la quantité d''espace disque utilisée et disponible.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('66','IFrame','en','The IFrame module displays content from another site within a frame on the site.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('6','Image','en','The Image module renders an image that has been uploaded to the File Manager. The height and width of images is exposed on the edit page, permitting Authorized User to alter the size of the rendered image.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('83','Members list','en','A module to search and show basic info about members.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('9','XML/XSL','en','The XML/XSL module displays the result of an XML/XSL transform. By combining the transformation language of XSL (Extensible Style Sheet Language) to the data contained in an XML (Extensible Markup Language) document, you can create multiple output formats from the same XML document.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('68','Google Map','en','Allow the user to have a quick link to Google Map.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('73','Search','en','The Search module displays a Search Box enabling authorized users to perform keyword searches of the site. The Search Results module MUST be added and set in order to perform a search.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('72','Paramètres d''hébergement','fr','Le webmestre peut gérer tous les paramètres maître pour le site d''hébergement.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('14','Site settings','en','The Site Settings page enables Administrators to configure the both basic and advanced setting for the site. Settings include design, advertising, user registration settings, etc.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('12','Security Roles','en','This page manages Security Roles for this web site.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('15','User Accounts','en','This User Accounts page manages User Accounts for this portal.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('19','Fournisseurs','fr','Le module sert à gérer les comptes fournisseurs et les oriflammes associés au portail. Ce module vous permet d''ajouter un nouveau, modifier, et supprimer un compte fournisseur.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('65','SQL','en','This page enable Hosts to make SQL queries to the database.')
INSERT INTO [dbo].[ModuleDefinitionsNames] ([ModuleDefID],[FriendlyName],[Language],[Description]) VALUES('88','Language','en','The Languages page displays the languages that are supported on the site.

This page also provides webmaster with access to the Language Editor. As well as enabling webmaster to add new languages, the Language Editor enables the editing of emails sent by the site (e.g. User Registration, Password Reminder, and Notification of Role Assignment) as well as site wide messages (e.g. Privacy Statement, Login Instructions, Terms Of Use).')

