/* add all stored procedures with dbo owner */

ALTER procedure [dbo].[GetMeteoCode]
@City nvarchar(50),
@Region nvarchar(50),
@Country nvarchar (50)
as
Declare @Code nvarchar(20)
Select @Code = Null
Select @Code = min(Code) 
from   MeteoMediaCode
where  City like '%' + @City + '%' and
Region like '%' + @Region + '%' and
Country like '%' + @Country + '%'
if @Code is null
begin
select @Code = min(Code)
from   MeteoMediaCode
where  City like '%' + @City + '%' and Region like '%' + @Region + '%'
end
if @Code is null
begin
select @Code = min(Code)
from   MeteoMediaCode
where  City like '%' + @City + '%' 
end
select 'Code' = @Code

