ALTER procedure [dbo].[AddNewLanguage]
@FromLanguage nvarchar(5),
@ToLanguage nvarchar(5),
@LDescription nvarchar(50),
@CultureCode nvarchar(5),
@Encoding nvarchar(20),
@Extra nvarchar(20)


as
declare

@Zone int,
@Description nvarchar(100),
@Code char(2),
@CCode char(3),
@ICode int,
@SettingName nvarchar(50),
@SettingValue nvarchar(256),
@LSettingValue nvarchar(4000),
@Context nvarchar (50)



----------------------------------------------------
-- Update
----------------------------------------------------
if not exists ( select * from language where language = @ToLanguage )
begin
insert language ( 
    
    Language,
    Description,
    CultureCode,
    Encoding 
  )
  values (
    @ToLanguage,
    @LDescription,
    @CultureCode,
    @Encoding
  )
end
else
begin
  update language
  set 
  Language = @ToLanguage,
  Description = @LDescription,
  CultureCode = @CultureCode,
  Encoding = @Encoding
  where language = @Tolanguage
end

----------------------------------------------------
-- TimeZone
----------------------------------------------------



select @Zone = min(Zone)
from   TimeZone
where  Language = @FromLanguage
while @Zone is not null 
begin
	if not exists ( select * from TimeZone where language = @ToLanguage and Zone = @Zone )
	begin
	select @Description = Description
	from   TimeZone
	where  Language = @FromLanguage and Zone = @Zone
	insert TimeZone ( 
		Zone,
		Language,
		Description 
		)
		values (
		@Zone,
		@ToLanguage,
		@Extra + @Description + @Extra
		)
	end
select @Zone = min(Zone)
from   TimeZone
where  Language = @FromLanguage and  Zone > @Zone 
end  

----------------------------------------------------
-- Country code
----------------------------------------------------

select @Code = min(Code)
from   CodeCountry
where  Language = @FromLanguage
while @Code is not null 
begin
	if not exists ( select * from CodeCountry where language = @ToLanguage and Code = @Code )
	begin
	select @Description = Description
	from   CodeCountry
	where  Language = @FromLanguage and Code = @Code
	insert CodeCountry ( 
		Code,
		Language,
		Description 
		)
		values (
		@Code,
		@ToLanguage,
		@Extra + @Description + @Extra
		)
	end
select @Code = min(Code)
from   CodeCountry
where  Language = @FromLanguage and  Code > @Code 
end  

----------------------------------------------------
-- Currency code
----------------------------------------------------

select @CCode = min(Code)
from   CodeCurrency
where  Language = @FromLanguage
while @CCode is not null 
begin
	if not exists ( select * from CodeCurrency where language = @ToLanguage and Code = @Code )
	begin
	select @Description = Description
	from   CodeCurrency
	where  Language = @FromLanguage and Code = @CCode
	insert CodeCurrency ( 
		Code,
		Language,
		Description 
		)
		values (
		@CCode,
		@ToLanguage,
		@Extra + @Description + @Extra
		)
	end
select @CCode = min(Code)
from   CodeCurrency
where  Language = @FromLanguage and  Code > @CCode 
end  


----------------------------------------------------
-- Frequency code
----------------------------------------------------

select @CCode = min(Code)
from   CodeFrequency
where  Language = @FromLanguage
while @CCode is not null 
begin
	if not exists ( select * from CodeFrequency where language = @ToLanguage and Code = @CCode )
	begin
	select @Description = Description
	from   CodeFrequency
	where  Language = @FromLanguage and Code = @CCode
	insert CodeFrequency ( 
		Code,
		Language,
		Description 
		)
		values (
		@CCode,
		@ToLanguage,
		@Extra + @Description + @Extra
		)
	end
select @CCode = min(Code)
from   CodeFrequency
where  Language = @FromLanguage and  Code > @CCode 
end  

----------------------------------------------------
-- Region code
----------------------------------------------------

select @Code = min(Code)
from   CodeRegion
where  Language = @FromLanguage
while @Code is not null 
begin
	if not exists ( select * from CodeRegion where language = @ToLanguage and Code = @Code )
	begin
	select @Description = Description
	from   CodeRegion
	where  Language = @FromLanguage and Code = @Code
	select @CCode = Country
	from   CodeRegion
	where  Language = @FromLanguage and Code = @Code
	insert CodeRegion ( 
		Code,
		Language,
		Description,
		Country 
		)
		values (
		@Code,
		@ToLanguage,
		@Extra + @Description + @Extra,
		@CCode
		)
	end
select @Code = min(Code)
from   CodeRegion
where  Language = @FromLanguage and  Code > @Code 
end  

----------------------------------------------------
-- Site Log Report CODE
----------------------------------------------------

select @ICode = min(Code)
from   CodeSiteLogReport
where  Language = @FromLanguage
while @ICode is not null 
begin
	if not exists ( select * from CodeSiteLogReport where language = @ToLanguage and Code = @ICode )
	begin
	select @Description = Description
	from   CodeSiteLogReport
	where  Language = @FromLanguage and Code = @ICode
	insert CodeSiteLogReport ( 
		Code,
		Language,
		Description
		)
		values (
		@ICode,
		@ToLanguage,
		@Extra + @Description + @Extra
		)
	End
select @ICode = min(Code)
from   CodeSiteLogReport
where  Language = @FromLanguage and  Code > @ICode 
end  

----------------------------------------------------
-- Language Settings
----------------------------------------------------

select @SettingName = min(SettingName)
from   languageSettings
where  Language = @FromLanguage
while  @SettingName is not null 
begin
	if not exists ( select * from languageSettings where language = @ToLanguage and SettingName = @SettingName )
	begin
	select @SettingValue = SettingValue
	from   languageSettings
	where  Language = @FromLanguage and SettingName = @SettingName
	select @Context = Context
	from   languageSettings
	where  Language = @FromLanguage and SettingName = @SettingName
	insert languageSettings ( 
		Language,
		SettingName,
		SettingValue,
		Context
		)
		values (
		@ToLanguage,
		@SettingName,
		@Extra+ @SettingValue + @Extra,    
		@Context
		)
	end
select @SettingName = min(SettingName)
from   languageSettings
where  Language = @FromLanguage and  SettingName > @SettingName 
end  

----------------------------------------------------
-- Long Language Settings
----------------------------------------------------

select @SettingName = min(SettingName)
from   longlanguageSettings
where  Language = @FromLanguage
while  @SettingName is not null 
begin
	if not exists ( select * from longlanguageSettings where language = @ToLanguage and SettingName = @SettingName )
	begin
	select @LSettingValue = SettingValue
	from   longlanguageSettings
	where  Language = @FromLanguage and SettingName = @SettingName
	insert longlanguageSettings ( 
		Language,
		SettingName,
		SettingValue
		)
		values (
		@ToLanguage,
		@SettingName,
		@Extra + @LSettingValue + @Extra
		)
	end
select @SettingName = min(SettingName)
from   longlanguageSettings
where  Language = @FromLanguage and  SettingName > @SettingName 
end  



exec UpdatelanguageContext @ToLanguage, 'culturecode', @CultureCode, 'language'
exec UpdatelanguageContext @ToLanguage, 'language', @LDescription, 'language'





