@Street		nvarchar(50),
@City		nvarchar(50),
@Region		nvarchar(50),
@PostalCode	nvarchar(50),
@Country	nvarchar(50),
@Telephone      nvarchar(50),
@Email		nvarchar(100),
@Username	nvarchar(100),
@Password	nvarchar(50),
@Authorized     bit,
@UserID	int	OUTPUT
as
declare @SuperUserId int, @TempUserId int, @RoleID int, @count int
select @SuperUserId = null
select @SuperUserId = UserId
from   Users
where  IsSuperUser = 1
and    Username = @Username
/* validate to make sure not using superusername */
if @SuperUserId is null
begin
select @count = count(Users.UserId)
from   UserPortals
inner join Users on UserPortals.UserId = Users.UserId
where  PortalID = @PortalID
and    Username = @Username
