ALTER procedure [dbo].[FindBanners]
@DisplayPortalId int,
@BannerTypeId int = null,
@SelectPortalId int = null,
@Banners  int = 1
as
declare @RecordCounter int
declare @RandomRecord int
declare @BannerId int
declare @StartDate smalldatetime
declare @EndDate smalldatetime
declare @Views int
declare @Impressions int
declare @VendorId int
declare @SiteLogHistory int
if @BannerTypeId is null
begin
  set @BannerTypeId = 1
end
/* purge vendor log */
select @SiteLogHistory = SiteLogHistory
from   Portals
where  PortalID = @DisplayPortalId
if @SiteLogHistory is not null
begin
  delete
  from   VendorLog
  where  PortalID = @DisplayPortalId
  and    datediff(day,DateTime,getdate()) > @SiteLogHistory
end
/* find number of banners */
select @RecordCounter = count(*)
from   Banners
inner join Vendors on Banners.VendorId = Vendors.VendorId
where  Banners.BannerTypeId = @BannerTypeId
and    ((Vendors.PortalId = @SelectPortalId) or (@SelectPortalId is null and Vendors.PortalId is null))
and    (Banners.Impressions > Banners.Views Or Banners.Impressions = 0)
and    (Banners.StartDate is null Or getdate() >= Banners.StartDate )
and    (Banners.EndDate is null Or getdate() <= Banners.EndDate )
if @Banners > @RecordCounter
begin
  select @Banners = @RecordCounter
end


CREATE TABLE #BannerIndex 
(
	IndexID		int IDENTITY (1, 1) NOT NULL,
	BannerID	int,
	BannerName  nvarchar(100),
    ImageFile   nvarchar(100)
)
-- INSERT into the temp table

declare @vSQL varchar(1000)
select @vSQL = 'INSERT INTO #BannerIndex (BannerID, BannerName, ImageFile) '
select @vSQL = @vSQL + 'select top ' + convert(varchar, @Banners) + ' BannerID, BannerName, ImageFile FROM Banners '
select @vSQL = @vSQL + 'inner join Vendors on Banners.VendorId = Vendors.VendorId '
If @SelectPortalId is Null
begin
select @vSQL = @vSQL + 'and    (Vendors.PortalId is null) '
end
else
begin
select @vSQL = @vSQL + 'and    (Vendors.PortalId = ' + convert(varchar, @SelectPortalId) + ')'
end

select @vSQL = @vSQL + 'and    (Banners.BannerTypeID = ' + convert(varchar, @BannerTypeID) + ') '
select @vSQL = @vSQL + 'and    (Banners.Impressions > Banners.Views Or Banners.Impressions = 0) '
select @vSQL = @vSQL + 'and    (Banners.StartDate is null Or getdate() >= Banners.StartDate ) '
select @vSQL = @vSQL + 'and    (Banners.EndDate is null Or getdate() <= Banners.EndDate )'
select @vSQL = @vSQL + ' ORDER BY NEWID()'
Execute (@vSQL)


Select * from #BannerIndex

/* update banners */
select @RecordCounter = 0
while @RecordCounter < @Banners
begin
select @RecordCounter = @RecordCounter + 1
select @BannerId = BannerID From #BannerIndex Where IndexID = @RecordCounter
  update Banners
  set    Views = Views + 1
  where  BannerId = @BannerId
  select @vendorId = VendorId
  from   Banners
  where  BannerId = @BannerId
  insert VendorLog (
    DateTime,
    PortalId,
    VendorId,
    BannerId,
    Search
  )
  values (
    getdate(),    @DisplayPortalId,
    @VendorId,
    @BannerId,
    null
  ) 
  select @StartDate = StartDate,
         @EndDate = EndDate,
         @Views = Views,
         @Impressions = Impressions
  from   Banners
  where  BannerId = @BannerId
  if @StartDate is null
    select @StartDate = getdate()
  if @Views = @Impressions
    select @EndDate = getdate()
  update Banners
  set    StartDate = @StartDate,
         EndDate = @EndDate
  where  BannerId = @BannerId
end



GO
