ALTER PROCEDURE [dbo].TTTForum_AddPost
(
	@ParentPostID	int,
	@ForumID		int,
	@UserID			int,
	@RemoteAddr		nvarchar(100),
	@Notify			bit,
	@Subject		nvarchar(255),
	@Body			ntext,
	@IsPinned		bit,
	@Image		nvarchar(255),
	@PostID			int OUTPUT
)
AS
DECLARE @MaxTreeSortOrder int
DECLARE @MaxFlatSortOrder int
DECLARE @ParentLevel int
DECLARE @ThreadID int
DECLARE @ParentTreeSortOrder int
DECLARE @NextTreeSortOrder int
DECLARE @PostDate datetime
DECLARE @PinnedDate datetime
DECLARE @ApprovedPost bit
DECLARE @ModeratedForum bit
SELECT @PostDate = GetDate()
-- Determine if this forum is moderated
SELECT @ModeratedForum = IsModerated FROM TTTForum_Forums (nolock) WHERE ForumID = @ForumID
IF @ModeratedForum = 0
	SELECT @ApprovedPost = 1
ELSE
	BEGIN
	-- with moderated forum, if this user is trusted the post will be approved
	SET @ApprovedPost = (SELECT IsTrusted FROM TTTForum_Users (nolock) WHERE DNNUserID = @UserID)
END
IF @ParentPostID = 0 -- New Post
	BEGIN		
	
		-- Do INSERT into Posts table
		INSERT
			TTTForum_Posts (ParentPostID, UserID, RemoteAddr, Notify, Subject, Body, PostDate, PostLevel, TreeSortOrder, FlatSortOrder, IsApproved)
		VALUES 
			(@ParentPostID, @UserID, @RemoteAddr, @Notify, @Subject, @Body, @PostDate, 0, 0, 0, @ApprovedPost)
		-- Create new thread if post is approved
		
			
		-- Get the new Post ID
		SELECT @PostID = @@IDENTITY
		SET @PinnedDate = @PostDate
		EXEC TTTForum_AddThread @PostID, @ForumID, @IsPinned, @PinnedDate, @Image, @ApprovedPost
		-- Update Posts with the new post id
		UPDATE 
			TTTForum_Posts
		SET 
			ThreadID = @PostID
		WHERE 
			PostID = @PostID
			
	END
ELSE -- @ParentPostID <> 0 means reply to an existing post
	BEGIN
		-- Get post information for what we are replying to
		SELECT 
			@ParentLevel = PostLevel,
			@ThreadID = ThreadID,
			@ParentTreeSortOrder = TreeSortOrder
		FROM 
			TTTForum_Posts
		WHERE 
			PostID = @ParentPostID
		-- Calculate maximum flat sort order
		SELECT
			@MaxFlatSortOrder = Max(FlatSortOrder)
		FROM
			TTTForum_Posts
		WHERE
			ThreadID = @ThreadID
		-- Is there another post at the same level or higher
		IF EXISTS (SELECT * 
				FROM TTTForum_Posts 
				WHERE PostLevel <= @ParentLevel 
				AND TreeSortOrder > @ParentTreeSortOrder
				AND ThreadID = @ThreadID )
			BEGIN
				-- Find the next post at the same level or higher
				SELECT 
					@NextTreeSortOrder = Min(TreeSortOrder)
				FROM 
					TTTForum_Posts
				WHERE 
					PostLevel <= @ParentLevel 
					AND TreeSortOrder > @ParentTreeSortOrder
					AND ThreadID = @ThreadID
				-- Move the existing posts down
				UPDATE 
					TTTForum_Posts
				SET 
					TreeSortOrder = TreeSortOrder + 1
				WHERE 
					ThreadID = @ThreadID
					AND TreeSortOrder >= @NextTreeSortOrder
				--  And put this one into place
				INSERT 
					TTTForum_Posts (ParentPostID, UserID, RemoteAddr, Notify, Subject, Body, PostDate, ThreadID, PostLevel, TreeSortOrder, FlatSortOrder, IsApproved)
				VALUES 
					(@ParentPostID, @UserID, @RemoteAddr, @Notify, @Subject, @Body, @PostDate, @ThreadID, @ParentLevel + 1, @NextTreeSortOrder, @MaxFlatSortOrder + 1, @ApprovedPost)
					
				-- Get the new post ID
				SELECT @PostID = @@IDENTITY
			END
		ELSE -- There are no posts at this level or above
			BEGIN
				-- Find the highest sort order for this parent
				SELECT 
					@MaxTreeSortOrder = MAX(TreeSortOrder)
				FROM 
					TTTForum_Posts
				WHERE 
					ThreadID = @ThreadID
				-- Insert the new post
				INSERT 
					TTTForum_Posts (ParentPostID, UserID, RemoteAddr, Notify, Subject, Body, PostDate, ThreadID, PostLevel, TreeSortOrder, FlatSortOrder, IsApproved)
				VALUES 
					(@ParentPostID, @UserID, @RemoteAddr, @Notify, @Subject, @Body, @PostDate, @ThreadID, @ParentLevel + 1, @MaxTreeSortOrder + 1, @MaxFlatSortOrder + 1, @ApprovedPost)
					
				-- Get the new post ID
				SELECT @PostID = @@IDENTITY
			END	
		IF @ApprovedPost = 1	-- update thread only if post is approved
			EXEC TTTForum_UpdateThread @ThreadID, @PostID, @IsPinned, @PostDate, @Image
	END
IF @ApprovedPost = 1	 -- it's not moderate forum
BEGIN
	-- update user postcount & forum post count
	EXEC TTTForum_IncrementUserPostCount @UserID
	EXEC TTTForum_UpdateForumPostInfo @ForumID, @PostID, @UserID
END
ELSE			-- post need to be moderated
BEGIN
	-- update forum moderated post count
	EXEC TTTForum_Moderate_ForumPostCount @ForumID, @PostID, @UserID, 'submit'
END

GO
