ALTER PROCEDURE [dbo].TTTForum_ForumCreateUpdateDelete
(
	
	@ForumGroupID int,
	@PortalID	int,
	@ModuleID	int,
	@Name		nvarchar(256),
	@Description	nvarchar(256),
	@CreatedByUser int,
	@IsModerated	bit,
	@IsActive	bit,
	@IsIntegrated	bit,
	@IntegratedGallery	int,
	@IntegratedAlbumName nvarchar(256),
	@IsPrivate	bit,
	@AuthorizedRoles	nvarchar(256),
	@Action	int,
	@ForumId	int OUT
)
AS
-- CREATE
IF @Action = 0
BEGIN
	DECLARE @SortOrder int
	SET @SortOrder = ISNULL((SELECT MAX(SortOrder) + 1 FROM TTTForum_Forums WHERE ForumGroupID = @ForumGroupID), 0)
	-- Create a new forum
	INSERT INTO 
		TTTForum_Forums 
		(
			ForumGroupID,
			PortalID,
			ModuleID,
			Name,	
			Description,
			CreatedByUser,
			IsModerated,
			IsActive,
			IsIntegrated,
			IntegratedGallery,
			IntegratedAlbumName,
			IsPrivate,
			AuthorizedRoles,
			SortOrder
		)
	VALUES 
		(			
			@ForumGroupID,
			@PortalID,
			@ModuleID,
			@Name	,
			@Description,
			@CreatedByUser,
			@IsModerated,
			@IsActive,
			@IsIntegrated,
			@IntegratedGallery,
			@IntegratedAlbumName	,
			@IsPrivate,
			@AuthorizedRoles,	
			@SortOrder
		)
		EXEC TTTForum_UpdateForumCount @ForumGroupID, 1
		SET @ForumID = @@IDENTITY
END
		
-- UPDATE
ELSE IF @Action = 1
BEGIN
	IF EXISTS(SELECT ForumID FROM TTTForum_Forums WHERE ForumID = @ForumID AND ForumGroupID = @ForumGroupID)
	BEGIN
		UPDATE
			TTTForum_Forums
		SET
			Name = @Name,
			Description = @Description,
			CreatedByUser = @CreatedByUser,
			IsModerated = @IsModerated,
			IsActive = @IsActive,
			IsIntegrated = @IsIntegrated,
			IntegratedGallery = @IntegratedGallery,
			IntegratedAlbumName = @IntegratedAlbumName,
			IsPrivate = @IsPrivate,
			AuthorizedRoles = @AuthorizedRoles
		
		WHERE ForumID = @ForumID	
	END
END
-- DELETE
ELSE IF @Action = 2
BEGIN
	DELETE TTTForum_Forums WHERE ForumID = @ForumID
	EXEC TTTForum_UpdateForumCount @ForumGroupID, 0
END

