ALTER PROCEDURE [dbo].TTTForum_GetPosts
(
	@ThreadID		int,
	@ThreadPage		int,
	@PostsPerPage		int,
	@FlatView		bit,
	@Filter			nvarchar
)
AS
DECLARE @StartSortOrder int
DECLARE @StopSortOrder int
SELECT @StartSortOrder = @ThreadPage*@PostsPerPage
SELECT @StopSortOrder = @StartSortOrder + @PostsPerPage - 1
IF (@FlatView = 1)
	BEGIN
		SELECT
			ParentPostID,	-- Post fields
			P.UserID,
			RemoteAddr,
			Notify,
			Subject,
			Body,
			PostDate,
			ThreadID,
			PostLevel,
			TreeSortOrder,
			FlatSortOrder,
			PostID,
			Alias,				-- User fields	
			UserAvatar,
			Avatar,
			LastModifiedDate,
			LastModifiedAuthorID,
			LastModifiedAuthor,
			PostCount
		FROM
			TTTForum_Posts P, TTTForum_Users U
		WHERE
			ThreadID = @ThreadID AND
			U.DNNUserID = P.UserID AND
			P.IsApproved = 1 AND
			P.IsLocked = 0 AND
			FlatSortOrder >= @StartSortOrder AND
			FlatSortOrder <= @StopSortOrder + @filter
		ORDER BY
			FlatSortOrder
	END
ELSE
	BEGIN
		SELECT
			ParentPostID,	-- Post fields
			P.UserID,
			RemoteAddr,
			Notify,
			Subject,
			Body,
			PostDate,
			ThreadID,
			PostLevel,
			TreeSortOrder,
			FlatSortOrder,
			PostID,
			Alias,				-- User fields			
			UserAvatar,
			Avatar,
			LastModifiedDate,
			LastModifiedAuthorID,
			LastModifiedAuthor,
			PostCount
		FROM
			TTTForum_Posts P, TTTForum_Users U
		WHERE
			ThreadID = @ThreadID AND
			U.DNNUserID = P.UserID AND
			P.IsApproved = 1 AND
			P.IsLocked = 0 AND
			TreeSortOrder >= @StartSortOrder AND
			TreeSortOrder <= @StopSortOrder + @filter
		ORDER BY
			TreeSortOrder
	END

