----------------------------------------------------

ALTER procedure [dbo].[TTTForum_GetThreads]
(
	@ForumID	int,
	@PageSize	int,
	@PageIndex	int,
	@filter		nvarchar(200)
)
AS
DECLARE @PageLowerBound int
DECLARE @PageUpperBound int
-- Set the page bounds
SET @PageLowerBound = @PageSize * @PageIndex
SET @PageUpperBound = @PageLowerBound + @PageSize + 1
-- Create a temp table to store the select results
CREATE TABLE #PageIndex 
(
	IndexID		int IDENTITY (1, 1) NOT NULL,
	ThreadID	int
)
-- INSERT into the temp table
INSERT INTO #PageIndex (ThreadID)
SELECT 
	T.ThreadID
FROM 
	TTTForum_Threads T INNER JOIN TTTForum_Posts P
	ON T.LastPostedPostID = P.PostID
WHERE 
	T.ForumID = @ForumID AND
	T.IsApproved = 1
ORDER BY 
	T.IsPinned DESC,  P.PostDate DESC
SELECT
	C.Subject AS Subject,
	A.Alias AS StartedByAlias,
	A.DNNUserID AS StartedByUserID,
	Replies,
	Views,
	B.Alias AS LastPostAlias,
	B.DNNUserID As LastPostUserID,
	D.PostDate AS DateLastPost,
	TTTForum_Threads.ThreadID AS ThreadID,
	TTTForum_Threads.ForumID,
	TTTForum_Threads.Image As Image,
	TTTForum_Threads.IsPinned AS Pinned,
    'URLField' = @filter + '&forumid=' + CONVERT(nvarchar, TTTForum_Threads.ForumID) + '&scope=post&threadid=' + CONVERT(nvarchar, TTTForum_Threads.ThreadID),
	D.Body As Body
FROM
	TTTForum_Threads, TTTForum_Posts C, TTTForum_Posts D, TTTForum_Users A, TTTForum_Users B, #PageIndex PageIndex
WHERE
	TTTForum_Threads.ThreadID				= PageIndex.ThreadID	AND
	PageIndex.IndexID				> @PageLowerBound		AND
	PageIndex.IndexID				< @PageUpperBound		AND
	TTTForum_Threads.ThreadID				= C.PostID				AND
	TTTForum_Threads.ForumID				= @ForumID				AND
	A.DNNUserID						= C.UserID				AND	
	TTTForum_Threads.LastPostedPostID		= D.PostID				AND
	C.ThreadID						= D.ThreadID			AND
	B.DNNUserID						= D.UserID
ORDER BY
	PageIndex.IndexID




GO
