IF (SELECT IsApproved FROM TTTForum_Posts (nolock) WHERE PostID = @PostID) = 1
BEGIN
	print 'Post is already approved'
	SELECT 0
	RETURN
END
ELSE
BEGIN
	print 'Post is not approved'
	-- Get details about the thread and forum this post belongs in
	SELECT		
		@ThreadID	 = ThreadID,
		@PostLevel	 = PostLevel,
		@PostDate  	= PostDate,
		@UserID	= UserID		
	FROM
		TTTForum_Posts
	WHERE
		PostID = @PostID
	
	-- Approve the post
	UPDATE 
		TTTForum_Posts
	SET 
		IsApproved 	= 1,
		IsLocked	= 0
	WHERE 
		PostID = @PostID
	
	IF @ThreadID = @PostID -- Also approve related thread if this is the first post of the thread
		BEGIN
			-- Get forumId from parent thread
			SELECT 
				@ForumID =  ForumID		
			FROM 
				TTTForum_Threads 
			WHERE 
				ThreadID = @ThreadID
			EXEC TTTForum_Moderate_ApproveThread @ForumID, @ThreadID
		END
	ELSE -- If this post is a reply or edit
		BEGIN
			SELECT  
				@ForumID =  ForumID,
				@IsPinned = IsPinned,
				@Image = [Image],
				@PostDate = GetDate()
			FROM TTTForum_Threads
			WHERE
				ThreadID = @ThreadID	
			
			EXEC TTTForum_UpdateThread @ThreadID, @PostID, @IsPinned, @PostDate, @Image
		END
	-- Update the user's post count & forum post count		
	EXEC TTTForum_IncrementUserPostCount @UserID	
	EXEC TTTForum_Moderate_ForumPostCount @ForumID, @PostID, @UserID, 'approve'	
	EXEC TTTForum_Moderate_ModeratorPostCount @ApprovedBy, @ForumID
	
	INSERT INTO
		TTTForum_ModerationAudit
	VALUES
		(GetDate(), @PostID, @ApprovedBy, 'Approve', @Notes)	
	
	-- Send back a success code
	-- SELECT 1
	
END

