ALTER PROCEDURE [dbo].TTTForum_Moderate_ForumPostCount
(
	@ForumID 	int,
	@PostID 	int ,	
	@PostAuthorID	 int, 
	@action	nvarchar(50)
)
AS
DECLARE @PostAuthor nvarchar (64)
IF @Action = 'submit' -- new moderate post queing
	BEGIN
		UPDATE
			TTTForum_Forums
		SET
			PostsToModerate = ((SELECT PostsToModerate FROM TTTForum_Forums WHERE ForumID = @ForumID) + 1)
		WHERE
			ForumID = @ForumID
	END
ELSE IF @Action = 'approve'		-- approved moderate post, increase forum post count, decrease posts to moderate count, update new post info into forum
	BEGIN
		SELECT 
			@PostAuthor = (Alias)
		FROM   
			TTTForum_Users
		WHERE 
			DNNUserID = @PostAuthorID
		UPDATE
			TTTForum_Forums
		SET
			PostsToModerate = ((SELECT PostsToModerate FROM TTTForum_Forums WHERE ForumID = @ForumID) - 1),
			TotalPosts = ((SELECT TotalPosts FROM TTTForum_Forums WHERE ForumID = @ForumID) + 1),
			MostRecentPostID = @PostID,
			MostRecentPostAuthorID = @PostAuthorID,
			MostRecentPostAuthor = @PostAuthor,
			MostRecentPostDate = GetDate()
		WHERE
			ForumID = @ForumID	
	END
ELSE	-- post is deleted or rejected
	BEGIN
		UPDATE
			TTTForum_Forums
		SET
			PostsToModerate = ((SELECT PostsToModerate FROM TTTForum_Forums WHERE ForumID = @ForumID) - 1)
		WHERE
			ForumID = @ForumID
	END

