ALTER procedure [dbo].TTTForum_PrivateMessage_GetInbox
@UserId       int,
@SortField varchar(100),
@SortDirection varchar(4)
AS
declare @strSQL nvarchar(1000)
declare @UserTimeZone int
declare @ServerTimeZone int
declare @TimeDiff int
Set @TimeDiff = '0'
Set @UserTimeZone = null
Set @ServerTimeZone = null
SELECT @UserTimeZone = TimeZone FROM TTTForum_Users WHERE TTTForum_Users.DNNUserID = @UserId
Select @ServerTimeZone = SettingValue  FROM HostSettings  WHERE HostSettings.SettingName = 'TimeZone'
if @UserTimeZone IS NOT NULL AND @ServerTimeZone IS NOT NULL 
Begin
Select @TimeDiff = @UserTimeZone - @ServerTimeZone
end
if @SortField <> ''  begin
	set @strSQL = 'SELECT PrivateMessages.MessageId, PrivateMessages.SenderId, PrivateMessages.ReceiverId,
			PrivateMessages.Subject, PrivateMessages.Message, PrivateMessages.MessageRead, 
			PrivateMessages.OriginalMessageId, DateAdd(minute, '  + cast(@TimeDiff as varchar(5)) + ',PrivateMessages.DateCreated) as DateCreated,
			Sender.Username AS SenderUsername, Receiver.Username AS ReceiverUsername 
			FROM PrivateMessages INNER JOIN Users Sender ON PrivateMessages.SenderId = Sender.UserID INNER JOIN Users Receiver ON PrivateMessages.ReceiverId = Receiver.UserID 
			WHERE PrivateMessages.receiverid = ' + cast(@UserId as varchar(10)) + ' and PrivateMessages.OriginalMessageId is null ORDER BY ' + @SortField + ' ' + @SortDirection
end
else begin
	set @strSQL = 'SELECT PrivateMessages.MessageId, PrivateMessages.SenderId, PrivateMessages.ReceiverId, PrivateMessages.Subject, PrivateMessages.Message, PrivateMessages.MessageRead, 
			PrivateMessages.OriginalMessageId, DateAdd(minute, '  + cast(@TimeDiff as varchar(5)) + ', PrivateMessages.DateCreated) as DateCreated,
