			FROM PrivateMessages INNER JOIN Users Sender ON PrivateMessages.SenderId = Sender.UserID INNER JOIN Users Receiver ON PrivateMessages.ReceiverId = Receiver.UserID 
			WHERE PrivateMessages.receiverid = ' + cast(@UserId as varchar(10)) + ' and PrivateMessages.OriginalMessageId is null ORDER BY PrivateMessages.MessageRead ASC, PrivateMessages.DateCreated DESC'
end
exec(@strSQL)

