			PrivateMessages.ReceiverId, PrivateMessages.Subject, PrivateMessages.Message, PrivateMessages.MessageRead, 
			PrivateMessages.OriginalMessageId, DateAdd(minute, '  + cast(@TimeDiff as varchar(5)) + ', PrivateMessages.DateCreated) as DateCreated,
			Sender.Username AS SenderUsername, Receiver.Username AS ReceiverUsername 
			FROM PrivateMessages INNER JOIN Users Sender ON PrivateMessages.SenderId = Sender.UserID INNER JOIN Users Receiver ON PrivateMessages.ReceiverId = Receiver.UserID 
			WHERE PrivateMessages.senderid = ' + cast(@UserId as varchar(10)) + ' and PrivateMessages.OriginalMessageId is not null ORDER BY ' + @SortField + ' ' + @SortDirection
end
else begin
	set @strSQL = 'SELECT PrivateMessages.MessageId, PrivateMessages.SenderId, PrivateMessages.ReceiverId, PrivateMessages.Subject,
			PrivateMessages.Message, PrivateMessages.MessageRead, 
			PrivateMessages.OriginalMessageId, DateAdd(minute, '  + cast(@TimeDiff as varchar(3)) + ', PrivateMessages.DateCreated) as DateCreated,
