ALTER PROCEDURE [dbo].[TTTForum_SearchGetResults]
(
	@WhereClause	nvarchar(3500),
	@PageIndex		int,
	@PageSize		int,
	@UserID		int,
	@PortalID		int
)
AS
-- Create a temp table to store the select results
CREATE TABLE #PageIndex 
(
	IndexID	int IDENTITY (1, 1) NOT NULL,
	PostID	int
)
-- Create dynamic SQL to populate temporary table
DECLARE @sql nvarchar(4000)
SELECT  @sql =	'INSERT INTO #PageIndex(PostID) SELECT TTTForum_Posts.PostID ' + 
				'FROM TTTForum_Posts (nolock), TTTForum_Threads (nolock) ' +
				'Left Outer Join TTTForum_Forums On TTTForum_Threads.ForumID = TTTForum_Forums.ForumID ' +
				'WHERE TTTForum_Posts.ThreadID = TTTForum_Threads.ThreadID ' +
				'AND TTTForum_Posts.IsApproved = 1 ' +
				'AND TTTForum_Posts.IsLocked = 0 ' +
				'AND TTTForum_Forums.PortalID = ' + CONVERT(varchar, @PortalID) + ' ' +
				@WhereClause + ' ORDER BY PostDate DESC'
EXEC(@sql)
-- All of the rows are inserted into the table - now select the correct subset
DECLARE @PageLowerBound int
DECLARE @PageUpperBound int
DECLARE @RecordCount	int
-- Set the page bounds
SET @PageLowerBound = @PageSize * @PageIndex
SET @PageUpperBound = @PageLowerBound + @PageSize + 1
SELECT @RecordCount = COUNT(*) FROM #PageIndex
-- Select the data out of the temporary table
SELECT
	PageIndex.PostID,
	TTTForum_Posts.ThreadID,
	TTTForum_Posts.Subject,
	TTTForum_Posts.PostDate,
	TTTForum_Users.Alias,
	TTTForum_Threads.ForumID,
	RecordCount = @RecordCount
FROM
	TTTForum_Users, TTTForum_Threads, TTTForum_Posts, #PageIndex PageIndex
WHERE
	TTTForum_Users.DNNUserID	= TTTForum_Posts.UserID	AND
	TTTForum_Threads.ThreadID	= TTTForum_Posts.ThreadID	AND
	(TTTForum_Threads.ForumID NOT IN (SELECT ForumID FROM TTTForum_PrivateForums) OR 
	ForumID IN (SELECT ForumID FROM TTTForum_PrivateForums WHERE RoleID IN (SELECT RoleID FROM UserRoles WHERE UserID = @UserID))) AND
	PageIndex.PostID		= TTTForum_Posts.PostID	AND
	PageIndex.IndexID		> @PageLowerBound		AND
	PageIndex.IndexID		< @PageUpperBound
ORDER BY
	PageIndex.IndexID



GO
