ALTER	PROCEDURE [dbo].TTTForum_Statistics_Get 
(
	@PortalID int ,
	@ModuleID int,
	@UpdateWindow int = 12
)
AS
-- Do we need to update the statistics?
DECLARE @LastUpdate datetime
DECLARE @DateWindow datetime
SET @LastUpdate = ISNULL((SELECT MAX(DateCreated) FROM TTTForum_Statistics_Site), '1/1/1797')
SET @DateWindow = DATEADD(hh, -@UpdateWindow, GetDate())
if (@LastUpdate <  @DateWindow)
	BEGIN
		exec TTTForum_Statistics_UpdateSite 0, 0, @PortalID, @ModuleID
		--exec TTTForum_Statistics_UpdateUserPostRank
	END
-- SELECT current statistics
SELECT TOP 1
	S.*,
	MostReadSubject = (SELECT Subject FROM TTTForum_Posts WHERE PostID = S.MostReadPostID),
	MostViewsSubject = (SELECT Subject FROM TTTForum_Posts WHERE PostID = S.MostViewsPostID),
	MostActiveSubject = (SELECT Subject FROM TTTForum_Posts WHERE PostID = S.MostActivePostID)
FROM
	TTTForum_Statistics_Site S (nolock)
WHERE
	ModuleID = @ModuleID
ORDER BY DateCreated DESC
-- SELECT TOP 10 Users
SELECT TOP 10
	U.UserID,
	U.Email,
	FUR.Alias,	
	FUR.LastForumsView,
	FUR.LastThreadView,
	FUR.LastActivity,	
	FUR.IsTrusted,
	FUR.PostCount	
FROM	
	TTTForum_Users FUR (nolock),
	Users U	
	(nolock)
	INNER JOIN UserPortals UP
	ON U.UserID = UP.UserID
WHERE	
	U.UserID = FUR.DNNUserID 
	AND FUR.PostCount > 0
	AND UP.PortalID = @PortalID
ORDER BY
	FUR.PostCount  DESC
-- SELECT top 10 Moderators
SELECT TOP 10
	U.UserID,
	U.Email,
	FUR.Alias,	
	FUR.LastForumsView,
	FUR.LastThreadView,
	FUR.LastActivity,	
	FUR.IsTrusted,
	FUR.PostCount,	
	M.PostsModerated
FROM
	TTTForum_Users FUR (nolock),	
	TTTForum_Moderators M (nolock),
	Users U
	(nolock)
	INNER JOIN UserPortals UP
	ON U.UserID = UP.UserID
WHERE
	U.UserID = FUR.DNNUserID AND
	M.UserID = FUR.DNNUserID AND	
	M.PostsModerated > 0 AND
	UP.PortalID = @PortalID
ORDER BY
	PostsModerated DESC
-- SELECT Moderator actions
SELECT
	Description,
	TotalActions
FROM
	TTTForum_ModerationAction (nolock)
ORDER BY 
	TotalActions DESC

