	--	exec forums_statistics_UpdateUserPostRank
	--IF @UpdateMostActiveUserList = 1
	--	exec forums_statistics_UpdateMostActiveUsers
	-- Total Anonymous Users 
	SET @TotalAnonymousUsers = 0
	-- SET @TotalAnonymousUsers = ISNULL((SELECT COUNT(UserID) FROM forums_AnonymousUsers), 0)
	
	-- Total Moderators 	
	SET @TotalModerators = ISNULL((	SELECT count(DISTINCT UserID) 
					FROM TTTForum_Moderators FM 
					(nolock)
					INNER JOIN TTTForum_Forums FF 
					(nolock)
					ON FM.ForumID = FF.ForumID
					WHERE FF.ModuleID = @ModuleID	
					), 0)
	
	-- Total Moderated Posts 	
	SET @TotalModeratedPosts = ISNULL((SELECT count(*) 
					FROM TTTForum_ModerationAudit MA					 	
					(nolock)	
					INNER JOIN UserPortals UP
					(nolock)
					ON MA.ModeratedBy = UP.UserID	
