					INNER JOIN UserPortals UP
					(nolock)
					ON FU.DNNUserID = UP.UserID	
					WHERE FU.EnableDisplayInMemberList = 1
					AND UP.PortalID = @PortalID
					ORDER BY 
					FU.PostCount DESC
					), 0)
	-- Newest user - no need in DNN	
	 SET @NewestUserID = 0
	-- SET @NewestUserID = ISNULL(
	--				(
	--					SELECT TOP 1 
	--						 U.UserID
	--					FROM 
	--						TTTForum_Users U,
	--						TTTForum_UserProfile P
	--					WHERE
	--						U.UserID = P.UserID AND
	--						P.EnableDisplayInMemberList = 1
	--					ORDER BY 
	--						DateCreated DESC
	--			), 0)
	-- Most read posts
	SET @MostReadPostID = 0	
	-- SET @MostReadPostID = ISNULL(
	--				(
	--					SELECT TOP 1 
	--						 T.ThreadID
