	--						T.ThreadID = P.PostID AND
	--						P.IsApproved = 1
	--					ORDER BY 
	--						(SELECT count(ThreadID) FROM TTTForum_ThreadsRead T WHERE P.PostID = T.ThreadID) DESC
	--				), 0)
	-- Other stats 
	SELECT
		-- Total Users		
		@TotalUsers = ISNULL( (SELECT COUNT(*) 
					FROM TTTForum_Users FU
					(nolock)
					Inner Join UserPortals U 
					(nolock)
					ON FU.DNNUserID = U.UserID					
					WHERE U.PortalID = @PortalID) ,0) ,
		-- Total Posts		
		--@TotalPosts = ISNULL( (SELECT COUNT(*) FROM TTTForum_Posts) + (SELECT COUNT(*) FROM TTTForum_PostsArchive (nolock)), 0),
		@TotalPosts = ISNULL( (Select SUM(Replies + 1) 
					FROM TTTForum_Threads T
					(nolock)
					INNER JOIN TTTForum_Forums FF
					(nolock)
					ON FF.ForumID = T.ForumID
					WHERE FF.ModuleID = @ModuleID
					AND T.IsApproved = 1), 0),
		-- Total Topics 		
		--@TotalTopics = ISNULL( (SELECT COUNT(*) FROM TTTForum_Threads) + (SELECT COUNT(*) FROM TTTForum_PostsArchive WHERE ParentID = PostID), 0),
		@TotalTopics = ISNULL( (SELECT COUNT(*) 
