					INNER JOIN TTTForum_Forums FF 
					(nolock)
					ON T.ForumID = FF.ForumID												
					WHERE FF.ModuleID = @ModuleID
					AND T.IsApproved = 1), 0),
		
		-- Total Posts in past 24 hours 		
		@NewPostsInPast24Hours = ISNULL( ( SELECT COUNT(*) 
							FROM TTTForum_Posts P 
							(nolock) 
							Inner JOIN TTTForum_Threads T 
							(nolock)
							ON T.ThreadID = P.ThreadID							
							Inner JOIN TTTForum_Forums FF
							(nolock)		
							ON FF.ForumID = T.ForumID											
							WHERE FF.ModuleID = @ModuleID
							AND P.IsApproved = 1 
							AND P.IsLocked = 0 
							AND PostDate > DATEADD(dd,-1,getdate())
							), 0),
		-- Total Users in past 24 hours 
		 @NewUsersInPast24Hours = 0,	
		--@NewUsersInPast24Hours = ISNULL(
		--					(SELECT COUNT(*) FROM TTTForum_Users (nolock) WHERE DateCreated > DATEADD(dd,-1,getdate())
		--				), 0),
		-- Total Topics in past 24 hours	
		 @NewThreadsInPast24Hours = ISNULL((SELECT COUNT(*) 
						FROM TTTForum_Posts P 
						(nolock) 
						Inner JOIN TTTForum_Threads T 
						(nolock)
						ON T.ThreadID = P.ThreadID							
						Inner JOIN TTTForum_Forums FF
						(nolock)		
						ON FF.ForumID = T.ForumID											
						WHERE FF.ModuleID = @ModuleID
						AND P.IsApproved = 1 
						AND P.ParentPostID = 0 
						AND P.PostDate > DATEADD(dd,-1,getdate())
						), 0)
	
	INSERT INTO TTTForum_statistics_Site
	SELECT 
		DateCreated = GetDate(),
		TotalUsers = @TotalUsers,
		TotalPosts = @TotalPosts,
		TotalModerators = @TotalModerators,
		TotalModeratedPosts = @TotalModeratedPosts,	
		TotalAnonymousUsers = @TotalAnonymousUsers,	
		TotalTopics = @TotalTopics,		
