ALTER PROCEDURE [dbo].TTTForum_UpdateForumGroupSortOrder
(
     @ForumGroupID int,
     @MoveUp bit
)
AS
BEGIN
DECLARE @currentSortValue int
DECLARE @replaceSortValue int
-- Get the current sort order
SELECT @currentSortValue = SortOrder FROM TTTForum_ForumGroups WHERE ForumGroupID = @forumGroupID
-- Move the item up or down?
IF (@MoveUp = 1)
  BEGIN
    IF (@currentSortValue != 0)
      BEGIN
        SET @replaceSortValue = @currentSortValue - 1
        UPDATE TTTForum_ForumGroups SET SortOrder = @currentSortValue WHERE SortOrder = @replaceSortValue
        UPDATE TTTForum_ForumGroups SET SortOrder = @replaceSortValue WHERE ForumGroupID = @forumGroupID
      END
  END
ELSE
  BEGIN
    IF (@currentSortValue < (SELECT MAX(ForumGroupID) FROM TTTForum_ForumGroups))
    BEGIN
      SET @replaceSortValue = @currentSortValue + 1
      UPDATE TTTForum_ForumGroups SET SortOrder = @currentSortValue WHERE SortOrder = @replaceSortValue
      UPDATE TTTForum_ForumGroups SET SortOrder = @replaceSortValue WHERE ForumGroupID = @forumGroupID
    END
  END
END

