ALTER PROCEDURE [dbo].TTTForum_UpdateForumSortOrder
(
     @ForumGroupID int,
     @ForumID int,
     @MoveUp bit
)
AS
BEGIN
DECLARE @currentSortValue int
DECLARE @replaceSortValue int
-- Get the current sort order
SELECT @currentSortValue = SortOrder FROM TTTForum_Forums 
	WHERE ForumGroupID = @ForumGroupID
	AND ForumID = @ForumID	
-- Move the item up or down?
IF (@MoveUp = 1)
  BEGIN
    IF (@currentSortValue != 0)
      BEGIN
        SET @replaceSortValue = @currentSortValue - 1
        UPDATE TTTForum_Forums SET SortOrder = @currentSortValue WHERE SortOrder = @replaceSortValue AND ForumGroupID = @ForumGroupID
        UPDATE TTTForum_Forums SET SortOrder = @replaceSortValue WHERE ForumID = @forumID AND ForumGroupID = @ForumGroupID
      END
  END
ELSE
  BEGIN
    IF (@currentSortValue < (SELECT MAX(ForumID) FROM TTTForum_Forums WHERE ForumGroupID = @ForumGroupID))
    BEGIN
      SET @replaceSortValue = @currentSortValue + 1
        UPDATE TTTForum_Forums SET SortOrder = @currentSortValue WHERE SortOrder = @replaceSortValue AND ForumGroupID = @ForumGroupID
        UPDATE TTTForum_Forums SET SortOrder = @replaceSortValue WHERE ForumID = @forumID AND ForumGroupID = @ForumGroupID
   END
  END
END

