/****** Object:  Stored Procedure dbo.UpdateArticlesPlusModules    Script Date: 10/24/2003 10:26:39 AM ******/
ALTER PROCEDURE dbo.UpdateArticlesPlusModules
(
 @strFlag as Char(1),
 @intArticlesPlusID as Int,
 @strRecords as NVarChar(2000)
)
AS
declare @localModuleID as int
declare @localActive as bit
declare @localCommaPos as int
declare @localLength as int
set @localLength = len(@strRecords)
while @localLength > 0
begin
 set @localCommaPos = charindex(',',@strRecords)
 if @localCommaPos > 0
  begin
   -- we'll assume there is at least one key there
   set @localModuleID = left(@strRecords, @localCommaPos-1)
   if len(@strRecords)=@localCommaPos -- have we come to the last, trailing comma?
    begin
     set @strRecords = ''
    end
   else
    begin
     set @strRecords = right(@strRecords,len(@strRecords)-@localCommaPos)
    end
   if @strFlag = 'U' -- Update
    begin
     set @localCommaPos = charindex(',',@strRecords)
     set @localActive = left(@strRecords, 1) -- constant 0 or 1
     if len(@strRecords)=@localCommaPos -- have we come to the last, trailing comma?
      begin
       set @strRecords = ''
      end
     else
      begin
       set @strRecords = right(@strRecords,len(@strRecords)-@localCommaPos)
      end
     if not exists (SELECT * FROM ArticlesPlusModule WHERE (ArticlesPlusID = @intArticlesPlusID) AND (ModuleID = @localModuleID))
      begin
       INSERT INTO ArticlesPlusModule
                   (ArticlesPlusID, ModuleID)
       VALUES      (@intArticlesPlusID, @localModuleID)
      end
     else
      begin
       UPDATE ArticlesPlusModule
       SET    ArticlesPlusID = @intArticlesPlusID, ModuleID = @localModuleID, APMActive = @localActive
       WHERE (ArticlesPlusID = @intArticlesPlusID) AND (ModuleID = @localModuleID)
      end
    end -- update
   else -- must not be updating so...
    begin -- removing records
     DELETE FROM ArticlesPlusModule
     WHERE     (ArticlesPlusID = @intArticlesPlusID) AND (ModuleID = @localModuleID)
    end
  end -- got a comma position
 else
  begin -- no comma position, so clear the string so loop will terminate
   set @strRecords = ''
  end 
 set @localLength = len(@strRecords)
end -- the big loop
RETURN 

