@PortalID int
as

DECLARE @container nvarchar(4000)
DECLARE @TitleContainer nvarchar(4000)
DECLARE @ModuleContainer nvarchar(4000)
DECLARE @containerAlignment nvarchar(4000)
DECLARE @containerColor nvarchar(4000)
DECLARE @containerBorder nvarchar(4000)

Select @container = PortalSettings.SettingValue from PortalSettings where SettingName = 'container' and PortalID = @PortalID
if @container is null Select @container = '[MODULE]'

Select @TitleContainer = PortalSettings.SettingValue from PortalSettings where SettingName = 'TitleContainer' and PortalID = @PortalID
if @TitleContainer is null Select @TitleContainer = '[MODULE]'

Select @ModuleContainer = PortalSettings.SettingValue from PortalSettings where SettingName = 'ModuleContainer' and PortalID = @PortalID
if @ModuleContainer is null Select @ModuleContainer = '[MODULE]'

Select @containerAlignment = PortalSettings.SettingValue from PortalSettings where SettingName = 'containerAlignment' and PortalID = @PortalID
if @containerAlignment is null Select @containerAlignment = ''

Select @containerColor = PortalSettings.SettingValue from PortalSettings where SettingName = 'containerColor' and PortalID = @PortalID
if @containerColor is null Select @containerColor = ''

Select @containerBorder = PortalSettings.SettingValue from PortalSettings where SettingName = 'containerBorder' and PortalID = @PortalID
if @containerBorder is null Select @containerBorder = ''


DECLARE @ModuleID int


select @ModuleID = min(ModuleID)
from   Modules, Tabs
where  Modules.TabId = Tabs.TabId 
and    Tabs.PortalId = @PortalID
while  @ModuleID is not null 
begin
delete
from   ModuleSettings
where  ModuleSettings.ModuleID = @ModuleID
and    (ModuleSettings.SettingName = 'container' or
		ModuleSettings.SettingName = 'TitleContainer' or
		ModuleSettings.SettingName = 'ModuleContainer' or
		ModuleSettings.SettingName = 'containerAlignment' or
		ModuleSettings.SettingName = 'containerColor' or
		ModuleSettings.SettingName = 'containerBorder')

  insert into ModuleSettings (
    ModuleID,
    SettingName,
    SettingValue
  ) 
  values (
    @ModuleID,
    'containerBorder',
    @containerBorder
  )
 
  insert into ModuleSettings (
    ModuleID,
    SettingName,
    SettingValue
  ) 
  values (
    @ModuleID,
    'containerColor',
    @containerColor
  )
 
  insert into ModuleSettings (
    ModuleID,
    SettingName,
    SettingValue
  ) 
  values (
    @ModuleID,
    'containerAlignment',
    @containerAlignment
  )
  
  insert into ModuleSettings (
    ModuleID,
    SettingName,
    SettingValue
  ) 
  values (
    @ModuleID,
    'ModuleContainer',
    @ModuleContainer
  )

  insert into ModuleSettings (
    ModuleID,
    SettingName,
    SettingValue
  ) 
  values (
    @ModuleID,
    'TitleContainer',
    @TitleContainer
  )

  insert into ModuleSettings (
    ModuleID,
    SettingName,
    SettingValue
  ) 
  values (
    @ModuleID,
    'container',
    @container
  )


 select @ModuleID = min(ModuleID)
 from   Modules, Tabs
 where  Modules.TabId = Tabs.TabId 
 and    Tabs.PortalId = @PortalID
 and  ModuleID > @ModuleID
end


