ALTER procedure [dbo].[UpdateTabModuleOrder]
@TabID           int
as
declare @PaneName nvarchar(50)
declare @ModuleCounter int
declare @ModuleOrder int
declare @Language nvarchar(5)


select @PaneName = min(PaneName)
from   Modules
where  TabID = @TabID
while @PaneName is not null 
begin
   select @Language = min(Language)  
   from Modules
   Where TabID = @TabID and
   PaneName = @PaneName
   while @Language is not null
   begin

  select @ModuleCounter = 0
  select @ModuleOrder = min(ModuleOrder)
  from   Modules
  where  TabID = @TabID
  and    PaneName = @PaneName
  and    Language = @Language
  and    ((ModuleOrder <> 0 and AllTabs = 1) or (AllTabs = 0))
  while @ModuleOrder is not null
  begin
    select @ModuleCounter = @ModuleCounter + 1        
    
	If   @Language <> '' 
    begin
    update Modules
    set    ModuleOrder = ((@ModuleCounter * 2) + 1) * -1 
    where  TabID = @TabID
    and    PaneName = @PaneName
    and    ModuleOrder = @ModuleOrder
    and    Language = @Language
    end  
    select @ModuleOrder = min(ModuleOrder)
    from   Modules
    where  TabID = @TabID
    and    PaneName = @PaneName
    and    Language = @Language
    and    ((ModuleOrder <> 0 and AllTabs = 1) or (AllTabs = 0))
    and    ModuleOrder > @ModuleOrder
  end 
  update Modules
  set    ModuleOrder = ModuleOrder * -1 
  where  TabID = @TabID
  and    PaneName = @PaneName
  and    Language = @Language
  and    Language <> ''
  select @Language = min(Language)
  from   Modules
  where  TabID = @TabID
  and    PaneName = @PaneName
  and    Language > @Language
  end 
  select @PaneName = min(PaneName)
  from   Modules
  where  TabID = @TabID
  and    PaneName > @PaneName
end





