ALTER PROCEDURE [dbo].UsersOnline_GetUsers

	@PortalID int,
	@LoggedInUserID int,
	@PageNumber int,
	@PageSize int,
	@SortOrder varchar(255),
	@SortDirection varchar(50)

AS
    CREATE TABLE #TempUsersOnline_Users
    (
        ID int IDENTITY PRIMARY KEY,
        SessionID 	nvarchar(255),
	UserName	nvarchar(100),
        UserID 		int,
	FirstName	nvarchar(50),
	LastName	nvarchar(50),
	CreatedDate	datetime,
	BuddyID		int
    )


    exec
    ('INSERT INTO #TempUsersOnline_Users
    (
        SessionID,
	Username,
        UserID,
	FirstName,
	LastName,
	CreatedDate,
	BuddyID
    )
	select SessionTracker.SessionID, Users.UserName, Users.UserID, Users.FirstName, Users.LastName, UserPortals.CreatedDate, (select BuddyID from UsersOnline_Buddies where UserID = ' + @LoggedInUserID + ' and BuddyID = Users.UserID) as "BuddyID"
	from Users
             left Outer Join UserPortals on Users.UserID = UserPortals.UserID
             left Outer Join SessionTracker on Users.UserID = SessionTracker.UserID
	where 
		
		UserPortals.PortalID = ' + @PortalID + 
	'Order BY ' + @SortOrder + ' ' + @SortDirection)

    -- declare two variables to calculate the 
    -- range of records to extract for the specified page
    DECLARE @FromID int
    DECLARE @ToID int

    -- calculate the first and last ID of the range of topics we need
    SET @FromID = ((@PageNumber - 1) * @PageSize) + 1
    SET @ToID = @PageNumber * @PageSize

    SELECT
	COUNT(*)
    FROM #TempUsersOnline_Users 

    -- select the page of records
    SELECT         
	ID,
        SessionID,
	Username,
        UserID,
	FirstName,
	LastName,
	CreatedDate,
	BuddyID	 
    FROM #TempUsersOnline_Users 
    WHERE ID >= @FromID AND ID <= @ToID

